export const MAKE_VISIBLE = "MAKE_VISIBLE";
export const MAKE_INVISIBLE = "MAKE_INVISIBLE";

export function makeVisible(col, s) {
  return {
    type: MAKE_VISIBLE,
    color: col,
    sound: s
  };
}

export function makeInvisible() {
  return {
    type: MAKE_INVISIBLE
  };
}
