import React from "react";
import "../styles/index.css";
import AppBar from "@material-ui/core/AppBar";
import PlayButton from "./PlayButton";
import { Line, Layer, Circle } from "react-konva";
import Toolbar from "@material-ui/core/Toolbar";
import { connect } from "react-redux";
import Konva from "konva";
import Tone from "tone";

class Cord extends React.Component {
  componentDidMount() {
    //playing around with creating a sound

    //create a synth and connect it to the master output (your speakers)
    // var distortion = new Tone.Distortion(3.5);
    // var tremolo = new Tone.Tremolo().start();
    // this.synth = new Tone.Synth().chain(distortion, tremolo, Tone.Master);

    this.synth = new Tone.Synth().toMaster();
    var max = window.innerHeight/2-50;
    var interval = max/8;
    var pts = [];
    var prevX = this.props.center.x+5;
    var prevY = this.props.center.y;
    for (var i=0; i<8; i++){
      pts.push(prevX);
      pts.push(prevY);
      prevY = prevY - interval;
    }
    this.tween = new Konva.Tween({
      node: this.line,
      points: pts,
      onFinish: function() {
        this.tween.reverse();
      }
      //   onReset: function() {
      //     this.tween.play();
      //   }
    });

    if (this.props.playing) {
      this.tween.play();
    }
  }

  playTween() {}

  componentDidUpdate(prevProps) {
    if (prevProps.sounds !== this.props.sounds) {
      this.synth.triggerAttackRelease(
        this.props.sounds[this.props.index],
        "4n"
      );
      this.tween.play();
    }
  }

  render() {
    var max = window.innerHeight/2-50;
    var interval = max/8;
    var pts = [];
    var prevX = this.props.center.x;
    var prevY = this.props.center.y;
    for (var i=0; i<8; i++){
      pts.push(prevX);
      pts.push(prevY);
      prevY = prevY - interval;
    }
    console.log(pts)
    return (
      <Layer>
        <Circle
          x={this.props.center.x}
          y={this.props.center.y - 400}
          radius={4}
          fill="#692D55"
        />
        <Circle
          x={this.props.center.x}
          y={this.props.center.y}
          radius={15}
          fill="#692D55"
        />
        <Line
          points={pts}
          stroke="#692D55"
          strokeWidth={2}
          tension={0.5}
          ref={node => {
            this.line = node;
          }}
        />
      </Layer>
    );
  }
}

function mapStateToProps(state) {
  return {
    center: state.shared.center,
    playing: state.shared.playing,
    sounds: state.cord.sounds,
    index: state.cord.index
  };
}

export default connect(mapStateToProps)(Cord);
