import React from "react";
import "../styles/index.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlusCircle, faMinusCircle } from "@fortawesome/free-solid-svg-icons";
import { connect } from "react-redux";
import { addLoop, updateLoop, activateLoop } from "../actions/loops";

const contentContainer = { padding: "1rem 0  0 2rem" };

class NewLoop extends React.Component {
  handlePlusClick = () => {
    // if loopCount is less than 5
    if (this.props.loopCount < 5) {
      this.props.dispatch(activateLoop(this.props.loopCount));
    }
  };
  handleMinusClick = () => {
    // if loopCount is less than 5
    if (this.props.loopCount < 5) {
      this.props.dispatch(deactivateLoop(this.props.loopCount));
    }
  };
  render() {
    return (
      <React.Fragment>
        <br />
        <div>
        <div className="tone-add" onClick={this.handlePlusClick}>
          <FontAwesomeIcon
            className="plus-icon inl-blk fa-lg"
            icon={faPlusCircle}
          />
        </div>
        </div>
        <div >
          <h3 className="light inl-blk">LOOP</h3>
        </div>
        <div className="tone-add" onClick={this.handleMinusClick}>
          <FontAwesomeIcon
            className="plus-icon inl-blk fa-lg"
            icon={faMinusCircle}
          />
        </div>
      </React.Fragment>
    );
  }
}

function mapStateToProps(state) {
  return {
    loops: state.loops,
    loopCount: state.shared.loopCount
  };
}

export default connect(mapStateToProps)(NewLoop);
