import React from "react";

class ToneButton extends React.Component {
  render() {
    return (
      <button
        style={{
          borderRadius: "100%",
          cursor: "pointer",
          backgroundColor: this.props.color,
          width: "50px"
        }}
      />
    );
  }
}

// const toneButtonStyle = {
//   position: "absolute",
