import React from "react";
import "../styles/index.css";
import { Layer } from "react-konva";

import { connect } from "react-redux";
import LoopInterval from "./LoopInterval";
import { addInterval } from "../actions/intervals";

class MountedIntervals extends React.Component {
    componentDidMount(){
        this.props.dispatch(addInterval(window.innerWidth/2, window.innerHeight/2, 10, 10, 1, 10))
    }
  render() {
    return (
      <Layer>
        {this.props.intervals.map(function(item) {
          return (
            <LoopInterval
              x={item.position.x}
              y={item.position.y}
              offset={item.offset}
              radius={item.radius}
              attachedLoop={item.attachedLoop}
            />
          );
        })}
      </Layer>
    );
  }
}

function mapStateToProps(state) {
  console.log(state); // state
  return {
    intervals: state.intervals
  };
}

export default connect(mapStateToProps)(MountedIntervals);
