import React from "react";
import "../styles/index.css";
import { Layer } from "react-konva";

import { connect } from "react-redux";
import LoopKonva from "./LoopKonva";
import { addLoop } from "../actions/loops";
import {addTone} from "../actions/tones";
import LoopInterval from "./LoopInterval";

class MountedLoops extends React.Component {
    componentDidMount(){
      var cx = window.innerWidth/2;
    var cy = window.innerHeight/2;
        this.props.dispatch(addLoop(window.innerHeight/3));
        this.props.dispatch(addTone(cx, cy, "#fcf6ec", "#ed1e79", 10, -this.props.radius, 0, 0, 20, null));
    this.props.dispatch(addTone(cx, cy, "#fcf6ec", "#ed1e79", 10, this.props.radius, 0, 0, 20, null));
    this.props.dispatch(addTone(cx, cy, "#fcf6ec", "#ed1e79", 10, 0, -this.props.radius, 0, 20, null));
    this.props.dispatch(addTone(cx, cy, "#fcf6ec", "#ed1e79", 10, 0, this.props.radius, 0, 20, null));
    }

    render() {
        return ( 
            <Layer>
            {this.props.loops.map(function(item) {
                return (
                    <LoopKonva radius={item.radius}/>
                );

            })}
            </Layer>
    );
  }
}

function mapStateToProps(state) {
  console.log(state); // state
  return {
    loops: state.loops
  };
}

export default connect(mapStateToProps)(MountedLoops);
