import React from "react";

const LibListStyle = {
  margin: "0 auto",
  textAlign: "center"
};

const LibListItemStyle = {
  display: "inline-block",
  verticalAlign: "top",
  padding: ".25rem"
};

const containerCard = {
  width: "5rem",
  height: "5rem"
};

class ToneButton extends React.Component {
  render() {
    return (
      <button
        style={{
          borderRadius: "100%",
          cursor: "pointer",
          backgroundColor: this.props.color,
          width: "2rem",
          height: "2rem",
          border: "none"
        }}
      />
    );
  }
}

class Library extends React.Component {
  render() {
    return (
      <ul style={LibListStyle}>
        {this.props.colors.map(color => (
          <li style={LibListItemStyle} key={color}>
            <ToneButton color={color} />
          </li>
        ))}
      </ul>
    );
  }
}

export default class LibraryContainer extends React.Component {
  render() {
    return (
      <div>
        <Library
          style={containerCard}
          colors={[
            "#fe6b12",
            "#fec52b",
            "#029e4e",
            "#e11a79",
            "#6581c9",
            "#7f47dd"
          ]}
        />
      </div>
    );
  }
}
