import {addLoop, updateLoop} from "../actions/loops"

class LoopKonva extends React.Component{
    render(){
        return(
            <Circle 
            x={window.innerWidth/2}
            y={window.innerHeight/2}
            radius={this.props.radius}
            fill={"transparent"}
            stroke={"#ed1e79"}
            strokeWidth={1.5}
            />
        )
    }
}
