import React from "react";
import "../styles/index.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faRedoAlt } from "@fortawesome/free-solid-svg-icons";
import { connect } from "react-redux";
import { trashAllLinear, trashAllAngular } from "../actions/shared";

const TrashButtonInactive = {
  color: "#ddd",
  position: "relative",
  padding: "0 0rem 0rem 0rem"
};
const TrashButtonActive = {
  color: "#692d55",
  position: "relative",
  padding: "0 0rem 0rem 0rem"
};

class TrashButton extends React.Component {
  render() {
    return (
      <React.Fragment>
        <div
          className="tone-add"
          onClick={
            this.props.mode === "angular"
              ? () => this.props.dispatch(trashAllAngular())
              : () => this.props.dispatch(trashAllLinear())
          }
        >
          <h3 className="inl-blk light ">RESET LOOP </h3>
          {this.props.playing ? (
            <FontAwesomeIcon
              className="inl-blk fa-lg"
              style={TrashButtonInactive}
              icon={faRedoAlt}
            />
          ) : (
            <FontAwesomeIcon
              className="inl-blk fa-lg"
              style={TrashButtonActive}
              icon={faRedoAlt}
            />
          )}
        </div>
      </React.Fragment>
    );
  }
}

function mapStateToProps(state) {
  return {
    playing: state.shared.playing,
    mode: state.shared.mode
  };
}

export default connect(mapStateToProps)(TrashButton);
