import React from "react";
import { connect } from "react-redux";
import { makeInvisible } from "../actions/cursor";

const tbFiller = {
  position: "absolute",
  zIndex: 5000,
  left: 0,
  top: 0,
  width: "100%",
  height: "100%",
  backgroundColor: "#000",
  cursor: "pointer !important"
};
class Cursor extends React.Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.props.dispatch(makeInvisible());
  }
  render() {
    return (
      <React.Fragment>
        <div
          style={{
            position: "absolute",
            left: this.props.cursorPos.x - 200,
            top: this.props.cursorPos.y - 150,
            width: 30,
            height: 30,
            zIndex: 100,
            borderRadius: "50%",
            pointerEvents: false,
            background: this.props.color
          }}
          onClick={() => this.handleClick}
        />
      </React.Fragment>
    );
  }
}

function mapStateToProps(state) {
  return {
    color: state.cursor.color,
    visible: state.cursor.visible
  };
}

export default connect(mapStateToProps)(Cursor);
