import { TOGGLE_PLAY, TOGGLE_STOP, TOGGLE_RESTART } from "../actions/shared";

export default function shared(state = {}, action) {
  switch (action.type) {
    case TOGGLE_PLAY:
      return Object.assign({}, state, {
        shared: {
          playing: true
        }
      });
    case TOGGLE_STOP:
      return Object.assign({}, state, {
        shared: {
          playing: false
        }
      });
    default:
      return state;
  }
}
