import { ADD_TONE, RECEIVE_TONES, RESIZE_TONE } from "../actions/tones";

export default function tones(state = [{id: 0, color: "#fff", attachedLoop: 1, radius: 10}], action) {
  switch (action.type) {
    case ADD_TONE:
      return [
        ...state,
        {
          id: action.id,
          color: action.color,
          // sound: "idk",
          attachedLoop: action.attachedLoop,
          radius: action.radius,
          sound: action.sound,
          position: action.position,
          offset: action.offset
        }
      ];

    case RESIZE_TONE:
      return { state };

    case RECEIVE_TONES:
      return {
        ...state,
        ...action.tones
      };
    default:
      return state;
  }
}
