import React from "react";
import "../styles/index.css";
import { Layer, Group } from "react-konva";

import { connect } from "react-redux";
import ToneKonva from "./ToneKonva";

class MountedTones extends React.Component {
  componentDidMount(){
    console.log("CHILD: " + JSON.stringify(this.layer.children))
    console.log("GROUP: " + this.gruppe)
    this.gruppe.rotate(3)
    console.log("FILTER: " + this.props.tones)

    console.log("FILTER: " + this.props.tones.filter(item => item.attachedLoop === 0))
    console.log("FILTER: " + this.props.tones.filter(function(item){return item.attachedLoop==0}))
    // console.log("CHILD1: " + this.child1)
  }

  render() {
    return (
      <Layer ref={node => { this.layer = node; }}>
        <Group ref={node => { this.gruppe = node; }}>
        {/* {this.props.tones.map(item => {
          return (
            <ToneKonva
              color={item.color}
              x={this.props.center.x}
              y={this.props.center.y}
              offset={item.offset}
              radius={item.radius}
              attachedLoop={item.attachedLoop}
              sound={item.sound}
              duration={item.duration}
              strokeWidth={item.strokeWidth}
              id={item.id}
              rotation={item.rotation}
              ref={node => { this.child = node; }}
            />
          );
        })} */}
        {this.props.tones.map(item => {
          return (
            <ToneKonva
              color={item.color}
              x={this.props.center.x}
              y={this.props.center.y}
              offset={item.offset}
              radius={item.radius}
              attachedLoop={item.attachedLoop}
              sound={item.sound}
              duration={item.duration}
              strokeWidth={item.strokeWidth}
              id={item.id}
              rotation={item.rotation}
              ref={node => { this.child = node; }}
            />
          );
        })}
        </Group>
      </Layer>
    );
  }
}

function mapStateToProps(state) {
  return {
    tones: state.tones,
    loops: state.loops,
    center: state.shared.center
  };
}

export default connect(mapStateToProps)(MountedTones);
