export const MAKE_VISIBLE = "MAKE_VISIBLE";
export const MAKE_INVISIBLE = "MAKE_INVISIBLE";

export function playTone(sound, sustain) {
  return {
    type: PLAY_TONE,
    sound: sound,
    duration: sustain
  };
}
