import {
  TOGGLE_PLAY,
  TOGGLE_STOP,
  TOGGLE_RESTART,
  TOGGLE_MUTE,
  TOGGLE_UNMUTE,
  TRASH_ALL_LINEAR,
  TRASH_ALL_ANGULAR,
  ADD_LOOP_COUNT,
  RESET_LOOP_COUNT,
  SCREEN_RESIZE,
  UPDATE_FILENAME,
  UPDATE_VOLUME,
  TOGGLE_MODE,
  UPDATE_TEMPO,
  UPDATE_SUSTAIN
} from "../actions/shared";

export default function shared(
  state = {
    visible: false,
    color: "transparent"
  },
  action
) {
  switch (action.type) {
    case MAKE_VISIBLE:
      return Object.assign({}, state, {
        visible: true,
        color: action.color
      });
    case MAKE_INVISIBLE:
      return Object.assign({}, state, {
        visible: false,
        color: "transparent"
      });

    default:
      return state;
  }
}
