import { ADD_INTERVAL } from "../actions/intervals";

export default function intervals(state = [], action) {
  switch (action.type) {
    case ADD_INTERVAL:
      return [
        ...state,
        {
          id: action.id,
          attachedLoop: action.attachedLoop,
          radius: action.radius,
          position: action.position,
          offset: action.offset
        }
      ];
    }
}
