import { ADD_LOOP, UPDATE_LOOP, RECEIVE_LOOPS, receiveLoops } from "../actions/loops";

export default function loops(state = {}, action) {
  switch (action.type) {
    case ADD_LOOP:
      return {
        ...state,
        [action.loop.id]: action.loop
      };
    case UPDATE_LOOP:
      return {
        ...state
        // something here
      };
    default:
      return state;
  }
}

export function handleInitialLoop(){
  return (dispatch) => {
    dispatch(receiveLoops(loops))
  }
}
