export const ADD_INTERVAL = "ADD_INTERVAL";

let nextIntervalId = 0;
export function addInterval(
  xCoord,
  yCoord,
  offsetx,
  offsety,
  attLoop,
  rad
) {
  return {
    type: ADD_INTERVAL,
    id: nextIntervalId++,
    position: {
      x: xCoord,
      y: yCoord
    },
    offset: {
      x: offsetx,
      y: offsety
    },
    attachedLoop: attLoop,
    radius: rad
  };
}
