import { PLAY_TONE } from "../actions/cord";
export default function cord(state = [], action) {
  switch (action.type) {
    case PLAY_TONE:
      return Object.assign({}, state, {
        index: action.index,
        sounds: [...state.sounds, action.sound]
      });
    default:
      return state;
  }
}
