import React from "react";
import "../styles/index.css";
import AppBar from "@material-ui/core/AppBar";
import PlayButton from "./PlayButton";
import { Line, Layer, Circle } from "react-konva";
import Toolbar from "@material-ui/core/Toolbar";
import { connect } from "react-redux";
import Konva from "konva";

class Cord extends React.Component {
  componentDidMount() {
    this.anim = new Konva.Animation(function(frame) {
      for (var n = 0; n < blobs.length; n++) {
        blobs[n].tension(
          amplitude * Math.sin((frame.time * 2 * Math.PI) / period) +
            centerTension
        );
      }
    }, layer);

    anim.start();
  }
  render() {
    return (
      <Layer>
        <Circle
          x={this.props.center.x}
          y={this.props.center.y - 400}
          radius={4}
          fill="#692D55"
        />
        <Circle
          x={this.props.center.x}
          y={this.props.center.y}
          radius={15}
          fill="#692D55"
        />
        <Line
          points={[
            this.props.center.x,
            this.props.center.y,
            this.props.center.x,
            this.props.center.y - 100,
            this.props.center.x,
            this.props.center.y - 200,
            this.props.center.x,
            this.props.center.y - 300,
            this.props.center.x,
            this.props.center.y - 400
          ]}
          stroke="#692D55"
          strokeWidth={3}
          tension={0}
        />
      </Layer>
    );
  }
}

function mapStateToProps(state) {
  return {
    center: state.shared.center
  };
}

export default connect(mapStateToProps)(Cord);
