import React from "react";
import { updateTempo } from "../actions/shared";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Slider from "@material-ui/core/Slider";
import { connect } from "react-redux";

const TempoSlider = withStyles({
  root: {
    color: "#692D54",
    height: 2
  },
  thumb: {
    width: 15,
    height: 15,
    "&:focus,&:hover,&$active": {
      boxShadow:
        "0 3px 1px rgba(0,0,0,0.1),0 4px 8px rgba(0,0,0,0.3),0 0 0 1px rgba(0,0,0,0.02)",

      backgroundColor: "#692D54"

      // Reset on touch devices, it doesn't add specificity
    }
  },
  // active: {},
  track: {
    height: 4,
    borderRadius: 4
  },
  rail: {
    height: 4,
    borderRadius: 4
  }
})(Slider);

class Tempo extends React.Component {
  constructor(props) {
    super(props);
    this.handleTempoChange = this.handleTempoChange.bind(this);
  }

  handleTempoChange(event, newValue) {
    this.props.dispatch(updateTempo(newValue));
  }
  render() {
    return (
      //   <div
      //     style={{
      //       display: "inline-block",
      //       position: "relative",
      //       marginRight: "auto",
      //       marginLeft: "0"
      //     }}
      //   >
      <React.Fragment>
        <h3 className="light inl-blk">TEMPO</h3>
        <div
          style={{
            padding: "1rem",
            width: "100px",
            float: "right",
            display: "block",
            alignItems: "center"
          }}
        >
          <TempoSlider
            defaultValue={1}
            onChange={this.handleTempoChange}
            aria-labelledby="continuous-slider"
            valueLabelDisplay="off"
            min={0.5}
            max={2}
            step={0.5}
            disabled={!this.props.playing ? false : true}
          />
          {/* <div
            style={{
              paddingLeft: "0.5rem",
              paddingRight: "0.5rem",
              width: "100px",
              display: "inline-block"
            }}
          >
            {this.props.tempo}
          </div> */}
        </div>
      </React.Fragment>
    );
  }
}

function mapStateToProps(state) {
  return {
    tempo: state.shared.tempo,
    playing: state.shared.playing
  };
}
const ConnectedTempo = connect(mapStateToProps)(Tempo);

export default ConnectedTempo;
