import React from "react";
import { connect } from "react-redux";

class Cursor extends React.Component {
  render() {
    return (
      <span style={{ cursor: "pointer" }}>
        <div
          style={{
            position: "absolute",
            left: this.props.cursorPos.x - 200,
            top: this.props.cursorPos.y - 150,
            width: 30,
            height: 30,
            zIndex: 100,
            borderRadius: "50%",
            background: this.props.color,
            pointerEvents: "none"
          }}
        />
      </span>
    );
  }
}

function mapStateToProps(state) {
  return {
    color: state.cursor.color,
    visible: state.cursor.visible
  };
}

export default connect(mapStateToProps)(Cursor);
