import { ADD_TONE, RECEIVE_TONES, RESIZE_TONE, UPDATE_TONE } from "../actions/tones";
import { strictEqual } from "assert";
import { stat } from "fs";

export default function tones(state = [], action) {
  switch (action.type) {
    case ADD_TONE:
      return [
        ...state,
        {
          id: action.id,
          color: action.color,
          stroke: action.stroke,
          strokeWidth: action.strokeWidth,
          attachedLoop: action.attachedLoop,
          radius: action.radius,
          sound: action.sound,
          position: action.position,
          offset: action.offset
        }
      ];

      // return new state with modified tone and everything else same
    case UPDATE_TONE:
      console.log(...state);
      const id = action.id;

      return [
        ...state.slice(0, id), {...state[id], color: action.color, stroke: "blue"}, ...state.slice(id+1)

        // console.log(...state)

        // ...state,

        //state[action.id].color = action.color


        // [action.id]: {
        //   ...state[action.id],
        //   color: action.color
        // }
        // state.id : {}

        // ...state[action.id],
        // color: action.color

        // [action.id.color]: action.color,
        // [action.id.stroke]: action.stroke
      ];
    case RESIZE_TONE:
      return { state };

    case RECEIVE_TONES:
      return {
        ...state,
        ...action.tones
      };
    default:
      return state;
  }
}
