import {
  TOGGLE_PLAY,
  TOGGLE_STOP,
  TOGGLE_RESTART,
  TOGGLE_MUTE,
  TOGGLE_UNMUTE,
  TRASH_ALL_LINEAR,
  TRASH_ALL_ANGULAR,
  ADD_LOOP_COUNT,
  RESET_LOOP_COUNT,
  SCREEN_RESIZE,
  UPDATE_FILENAME,
  UPDATE_VOLUME,
  TOGGLE_MODE,
  UPDATE_TEMPO,
  UPDATE_SUSTAIN
} from "../actions/shared";

export default function shared(
  state = {
    playing: false,
    center: {
      x: 380 + (window.innerWidth - 380) / 2,
      y: window.innerHeight / 2
    },
    screenWidth: window.innerWidth,
    screenHeight: window.innerHeight,
    loopCount: 1,
    muted: false,
    mode: "angular",

    selectedSustain: "8n",
    fileName: "MyProject",
    volume: 0,
    tempo: 110,
    toneSizes: {
      "32n": 10,
      "16n": 14,
      "8n": 16,
      "4n": 18,
      "2n": 20,
      "1m": 25,
      "2m": 30
    }
  },
  action
) {
  switch (action.type) {
    case TOGGLE_PLAY:
      return Object.assign({}, state, {
        playing: true
      });

    default:
      return state;
  }
}
