import React from "react";
import "../styles/index.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faVolumeMute, faVolumeUp } from "@fortawesome/free-solid-svg-icons";
import { connect } from "react-redux";
import { toggleMute, toggleUnmute } from "../actions/shared";
import Toolbar from "@material-ui/core/Toolbar";
import Grid from "@material-ui/core/Grid";
import Slider from "@material-ui/core/Slider";
import VolumeDown from "@material-ui/icons/VolumeDown";
import VolumeUp from "@material-ui/icons/VolumeUp";
import { ThemeProvider } from "@material-ui/styles";
import { createMuiTheme } from "@material-ui/core/styles";
// const MuteButtonStyle = {
//   color: "#692d55",
//   position: "relative",
//   margin: "0 auto 0 0 "
// };

class MuteButton extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: 30 };
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(event, newValue) {
    this.setState({ value: newValue });
  }

  render() {
    return (
      <div>
        <VolumeUp style={{}} />
        <div
          style={{
            paddingLeft: "0.5rem",
            width: "100px",
            float: "right",
            color: "#692d55"
          }}
        >
          <Slider
            defaultValue={50}
            classes={{
              rail: { color: "green" }, // class name, e.g. `classes-nesting-root-x`
              label: { color: "green" } // class name, e.g. `classes-nesting-label-x`
            }}
            value={this.props.value}
            onChange={() => this.handleChange()}
            aria-labelledby="continuous-slider"
          />{" "}
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  console.log(state); // state
  return {
    muted: state.shared.muted
  };
}

export default connect(mapStateToProps)(MuteButton);
