import React from "react";
import "../styles/index.css";

import { connect } from "react-redux";

import ToggleButton from "@material-ui/lab/ToggleButton";
import ToggleButtonGroup from "@material-ui/lab/ToggleButtonGroup";

class ToggleMode extends React.Component {
  handleClick = () => {
    this.props.dispatch(toggleMode());
  };
  render() {
    return (
      <React.Fragment>
        <br />
        <ToggleButtonGroup value={3} exclusive onChange={this.handleChange}>
          <ToggleButton>Linear</ToggleButton>
          <ToggleButton>Angular</ToggleButton>
        </ToggleButtonGroup>
      </React.Fragment>
    );
  }
}

function mapStateToProps(state) {
  return {
    mode: state.shared.mode
  };
}

export default connect(mapStateToProps)(NewLoop);
