import React from "react";
import "../styles/index.css";
import { Layer, Group } from "react-konva";
import Konva from "konva";
import { connect } from "react-redux";
import ToneKonva from "./ToneKonva";

class MountedTones extends React.Component {
  componentDidMount(){
    this.angularSpeed = 70;
    this.anim = new Konva.Animation(frame => {
      // frame.timeDiff = 30;
      // frame.frameRate = 60;
      var tDiff = frame.timeDiff;
      var angleDiff = (tDiff * this.angularSpeed) / 1000;
      // rotate the group
      this.group0.rotate(angleDiff);
      // console.log("frame.time for " + this.props.id + ": " + frame.time)
      this.trueTime = frame.time - this.lastTime;
      // console.log("angleDiff for " + this.props.id + ": " + angleDiff)
      console.log("angleSpeed for " + this.props.id + ": " + this.angularSpeed)

    }, this.group1.getLayer());
  }

  componentDidUpdate(prevProps){
    if (prevProps.tones !== this.props.tones){
      console.log("TONES update: " + this.props.tones)
    }
    if (prevProps.playing !== this.props.playing) {
      if (this.props.playing) {
        console.log("anim start")
        this.anim.start();
      } else {
        this.anim.isRunning() && this.anim.stop();
      }
    }
  }

  render() {
    // if (this.props.tones.length === 112){
    console.log("TONES updateddd: " + this.props.tones)
    console.log("FILTER: " + this.props.tones.filter(item => item.attachedLoop === 0))
    const loop0 = this.props.tones.filter(item => item.attachedLoop === 0)
    const loop1 = this.props.tones.filter(item => item.attachedLoop === 1)
    // console.log(loop0)
    return (
      <Layer ref={node => { this.layer = node; }}>
        <Group  ref={node => { this.group0 = node; }}>
        {/* {this.props.tones.map(item => {
          return (
            <ToneKonva
              color={item.color}
              x={this.props.center.x}
              y={this.props.center.y}
              offset={item.offset}
              radius={item.radius}
              attachedLoop={item.attachedLoop}
              sound={item.sound}
              duration={item.duration}
              strokeWidth={item.strokeWidth}
              id={item.id}
              rotation={item.rotation}
              ref={node => { this.child = node; }}
            />
          );
        })} */}
        {loop0.map(item => {
          return (
            <ToneKonva
              color={item.color}
              x={this.props.center.x}
              y={this.props.center.y}
              offset={item.offset}
              radius={item.radius}
              attachedLoop={item.attachedLoop}
              sound={item.sound}
              duration={item.duration}
              strokeWidth={item.strokeWidth}
              id={item.id}
              rotation={item.rotation}
              ref={node => { this.child = node; }}
            />
          );
        })}
        </Group>
        <Group ref={node => { this.group1 = node; }}>
        {/* {this.props.tones.map(item => {
          return (
            <ToneKonva
              color={item.color}
              x={this.props.center.x}
              y={this.props.center.y}
              offset={item.offset}
              radius={item.radius}
              attachedLoop={item.attachedLoop}
              sound={item.sound}
              duration={item.duration}
              strokeWidth={item.strokeWidth}
              id={item.id}
              rotation={item.rotation}
              ref={node => { this.child = node; }}
            />
          );
        })} */}
        {loop1.map(item => {
          return (
            <ToneKonva
              color={item.color}
              x={this.props.center.x}
              y={this.props.center.y}
              offset={item.offset}
              radius={item.radius}
              attachedLoop={item.attachedLoop}
              sound={item.sound}
              duration={item.duration}
              strokeWidth={item.strokeWidth}
              id={item.id}
              rotation={item.rotation}
              ref={node => { this.child = node; }}
            />
          );
        })}
        </Group>
      </Layer>
    );
      // } else {
      //   return null
      // }
  }
}

function mapStateToProps(state) {
  return {
    tones: state.tones,
    loops: state.loops,
    center: state.shared.center,
    playing: state.shared.playing
  };
}

export default connect(mapStateToProps)(MountedTones);
