import React, { Component } from "react";
import { connect } from "react-redux";
import NewLoop from "./NewLoop";
import "../styles/index.css";
import NewToneMenu from "./NewToneMenu";
import ToneButton from "./ToneButton";
import Download from "./Download";
import TextField from "@material-ui/core/TextField";
import Upload from "./Upload";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPlusCircle,
  faTerminal,
  faGlobeAmericas
} from "@fortawesome/free-solid-svg-icons";
import { tsConstructorType } from "@babel/types";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Radio from "@material-ui/core/Radio";

import {
  red,
  pink,
  purple,
  indigo,
  blue,
  cyan,
  teal,
  green,
  yellow,
  amber,
  orange,
  deepOrange
} from "@material-ui/core/colors/";

const colorHues = [
  red,
  pink,
  purple,
  indigo,
  blue,
  cyan,
  teal,
  green,
  yellow,
  amber,
  orange,
  deepOrange
];
const LibListStyle = {
  textAlign: "left",
  margin: 0,
  zIndex: 1,
  padding: 0,
  position: "relative"
};

const MenuItemStyle = {
  display: "block",
  margin: "0 auto",
  color: "#AB8FA2",
  paddingBottom: "2rem "
};
const MenuItemStyleActive = {
  display: "block",
  margin: "0 auto",

  color: "#E6E6E6",
  paddingBottom: "2rem "
};

const LeftNavMenu = {
  marginTop: "20vh"
};
const LibListItemStyle = {
  display: "inline-block",
  verticalAlign: "top",
  padding: ".25rem"
};

const CardStyle = {
  position: "absolute",
  top: "0%",
  left: "4%",
  width: "15rem",
  height: "100vh",
  backgroundColor: "#fff",
  borderRadius: "1%",
  boxShadow: "0 20px 10px rgba(0,0,0,0.01), 0 6px 6px rgba(0,0,0,0.05)"
};

const LeftNavStyle = {
  position: "absolute",
  top: "0%",
  left: "0%",
  width: "4vw",
  height: "100vh",
  backgroundColor: "#692D55"
};

var Buttons = [];
var octave = 4;
var sounds = ["A", "A#", "B", "C", "C#", "D", "D#", "E", "F", "F#", "G", "G#"];
for (let i = 0; i < colorHues.length; i++) {
  Buttons.push({
    color: colorHues[i][octave * 100],
    sound: sounds[i] + octave
  });
}

// const Buttons = [
//   { color: `hsl(5,   ${lightness}%, 67%)`, sound: "A4" },
//   { color: `hsl(25,  ${lightness}%,  99%)`, sound: "A#4" },
//   { color: `hsl(47,  ${lightness}%,  95%)`, sound: "B4" },
//   { color: `hsl(80,  ${lightness}%,  68%)`, sound: "C4" },
//   { color: `hsl(110, ${lightness}%,  50%)`, sound: "C#4" },
//   { color: `hsl(160, ${lightness}%,  57%)`, sound: "D4" },
//   { color: `hsl(190, ${lightness}%,  77%)`, sound: "D#4" },
//   { color: `hsl(210, ${lightness}%,  54%)`, sound: "E4" },
//   { color: `hsl(240, ${lightness}%,  55%)`, sound: "F4" },
//   { color: `hsl(270, ${lightness}%,  83%)`, sound: "F#4" },
//   { color: `hsl(298, ${lightness}%,  70%)`, sound: "G4" },
//   { color: `hsl(330, ${lightness}%, 75%)`, sound: "G#4" }
// ];

const contentContainer = { padding: "2rem", position: "relative" };

class Library extends React.Component {
  constructor(props) {
    super(props);
    this.state = { octave: 4 };
    this.octaves = [1, 2, 3, 4, 5, 6, 7];
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({ octave: parseInt(event.target.value) });
  }
  render() {
    return (
      <React.Fragment>
        <br />

        {this.octaves.map(item => (
          <Radio
            checked={this.state.octave === item}
            onChange={this.handleChange}
            value={item}
            inputProps={{ "aria-label": { item } }}
          />
        ))}

        <ul style={LibListStyle}>
          {this.props.colors.map(item => (
            <li style={LibListItemStyle} key={item.color}>
              <ToneButton color={item.color} sound={item.sound} />
            </li>
          ))}
        </ul>
      </React.Fragment>
    );
  }
}

class LibraryContainer extends React.Component {
  render() {
    return (
      <React.Fragment>
        <h3 className="light inl-blk"> TONES</h3>
        {/* <NewToneMenu /> */}

        <Library colors={Buttons} />
      </React.Fragment>
    );
  }
}

class CreateMenu extends React.Component {
  render() {
    return (
      <React.Fragment>
        <LibraryContainer />
        <NewLoop />
      </React.Fragment>
    );
  }
}
class TerminalMenu extends React.Component {
  render() {
    return (
      <React.Fragment>
        <h3> Welcome to the terminal.</h3>
        <p> This feature is still in development. Check back soon! </p>
        <TextField
          id="outlined-multiline-static"
          multiline
          label="Terminal"
          rows="20"
          defaultValue="Default Value"
          margin="normal"
          variant="filled"
        />
      </React.Fragment>
    );
  }
}

const ProjectField = withStyles({
  root: {
    "& label.Mui-focused": {
      color: "#692D54"
    },
    "& .MuiInput-underline:after": {
      borderBottomColor: "#692D54"
    },
    "& .MuiOutlinedInput-root": {
      "&.Mui-focused fieldset": {
        borderColor: "#692D54"
      }
    }
  }
})(TextField);

class ShareMenu extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "MyProject"
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({ name: event.target.value });
    console.log(this.state.name);
  }
  render() {
    return (
      <React.Fragment>
        <ProjectField
          id="standard-name"
          label="Project Name"
          defaultValue={this.state.name}
          value={this.state.name}
          onChange={this.handleChange}
          margin="normal"
        />
        <Download name={this.state.name} />
        <Upload />
      </React.Fragment>
    );
  }
}

export default class LeftNav extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showing: "createMenu"
    };
    this.handleShare = this.handleShare.bind(this);
    this.handleCreate = this.handleCreate.bind(this);
    this.handleTerminal = this.handleTerminal.bind(this);
    this.showMenu = this.showMenu.bind(this);
  }

  handleShare() {
    this.setState({ showing: "shareMenu" });
  }
  handleTerminal() {
    this.setState({ showing: "terminalMenu" });
  }
  handleCreate() {
    this.setState({ showing: "createMenu" });
  }
  showMenu() {
    switch (this.state.showing) {
      case "createMenu":
        return <CreateMenu />;
      case "shareMenu":
        return <ShareMenu />;
      case "terminalMenu":
        return <TerminalMenu />;
      default:
        return null;
    }
  }

  render() {
    return (
      <React.Fragment>
        <div style={LeftNavStyle}>
          <div style={LeftNavMenu}>
            <FontAwesomeIcon
              className="inl-blk fa-2x menu-item"
              style={
                this.state.showing === "createMenu"
                  ? MenuItemStyleActive
                  : MenuItemStyle
              }
              icon={faPlusCircle}
              onClick={() => this.handleCreate()}
            />
            <FontAwesomeIcon
              className="inl-blk fa-lg  menu-item"
              style={
                this.state.showing === "terminalMenu"
                  ? MenuItemStyleActive
                  : MenuItemStyle
              }
              icon={faTerminal}
              onClick={() => this.handleTerminal()}
            />
            <FontAwesomeIcon
              className="inl-blk fa-2x  menu-item"
              style={
                this.state.showing === "shareMenu"
                  ? MenuItemStyleActive
                  : MenuItemStyle
              }
              icon={faGlobeAmericas}
              onClick={() => this.handleShare()}
            />
          </div>
        </div>
        <div style={CardStyle}>
          <div style={contentContainer}>
            <img
              alt="logo"
              src={require("../Logo.png")}
              style={{
                width: "7.5rem",
                margin: "0",
                display: "block",
                paddingBottom: "4rem"
              }}
            />

            <span className="logo-spacer" />

            <div>{this.showMenu()}</div>
            <div />
          </div>
        </div>
      </React.Fragment>
    );
  }
}
