import React from "react";
import "../styles/index.css";
import AppBar from "@material-ui/core/AppBar";
import PlayButton from "./PlayButton";
import MuteButton from "./MuteButton";
import TrashButton from "./TrashButton";
import Toolbar from "@material-ui/core/Toolbar";
import { connect } from "react-redux";
import { updateTempo } from "../actions/shared";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Slider from "@material-ui/core/Slider";
const NavStyleOuter = {
  position: "absolute",
  top: "auto",
  bottom: "2.5vh",
  height: "5rem",
  width: "100vw"
};

const ToolbarStyle = {
  top: "50%"
};

const TempoSlider = withStyles({
  root: {
    color: "#692D54",
    height: 2
  },
  thumb: {
    width: 15,
    height: 15,
    "&:focus,&:hover,&$active": {
      boxShadow:
        "0 3px 1px rgba(0,0,0,0.1),0 4px 8px rgba(0,0,0,0.3),0 0 0 1px rgba(0,0,0,0.02)",

      backgroundColor: "#692D54"

      // Reset on touch devices, it doesn't add specificity
    }
  },
  // active: {},
  track: {
    height: 4,
    borderRadius: 4
  },
  rail: {
    height: 4,
    borderRadius: 4
  }
})(Slider);

class BottomNav extends React.Component {
  constructor(props) {
    super(props);
    this.handleTempoChange = this.handleTempoChange.bind(this);
  }
  handleTempoChange(event, newValue) {
    this.props.dispatch(updateTempo(newValue));
  }
  render() {
    return (
      <div style={NavStyleOuter}>
        <div
          style={{
            width: "50vw",
            margin: `0 ${this.props.center - window.innerWidth / 4}px`,
            backgroundColor: "#fff",
            borderRadius: "100px",
            boxShadow:
              "0 20px 10px rgba(0,0,0,0.01), 0 6px 6px rgba(0,0,0,0.05)"
          }}
        >
          <Toolbar style={ToolbarStyle}>
            {/* <InstrumentSelect /> */}
            <PlayButton />
            <MuteButton />
            <TrashButton />
            <div
              style={{
                paddingRight: "0.5rem",
                display: "inline-block",
                width: "100px",
                position: "absolute",
                right: 0,
                float: "right",
                color: "#692d55"
              }}
            >
              <TempoSlider
                defaultValue={1}
                onChange={this.handleTempoChange}
                aria-labelledby="continuous-slider"
                valueLabelDisplay="off"
                min={0.5}
                max={2}
                step={0.5}
                marks
                disabled={!this.props.playing ? false : true}
              />
              <h4 className="inl-blk">{this.props.tempo}</h4>
            </div>
          </Toolbar>
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    center: state.shared.center.x,
    tempo: state.shared.tempo
  };
}
const ConnectedBottomNav = connect(mapStateToProps)(BottomNav);

export default ConnectedBottomNav;
