import React from "react";
import "../styles/index.css";
import { Layer } from "react-konva";

import { connect } from "react-redux";
import ToneKonva from "./ToneKonva";

class MountedTones extends React.Component {
  componentDidMount(){
    console.log("CHILD: " + this.layer.child)
    // console.log("CHILD1: " + this.child1)
  }

  render() {
    return (
      <Layer ref={node => { this.layer = node; }}>
        {/* <ToneKonva
              color={"#fff"}
              x={this.props.center.x}
              y={this.props.center.y}
              offset={{x:0, y:0}}
              radius={10}
              attachedLoop={0}
              sound={null}
              duration={0}
              strokeWidth={0}
              id={200}
              rotation={0}
              ref={(node) => { this.child1 = node; }}
            /> */}
        {this.props.tones.map(item => {

            <ToneKonva
              color={item.color}
              x={this.props.center.x}
              y={this.props.center.y}
              offset={item.offset}
              radius={item.radius}
              attachedLoop={item.attachedLoop}
              sound={item.sound}
              duration={item.duration}
              strokeWidth={item.strokeWidth}
              id={item.id}
              rotation={item.rotation}
              ref={node => { this.child = node; }}
            />

        })}
      </Layer>
    );
  }
}

function mapStateToProps(state) {
  return {
    tones: state.tones,
    loops: state.loops,
    center: state.shared.center
  };
}

export default connect(mapStateToProps)(MountedTones);
