import React from "react";
import "../styles/index.css";

import Draggable from "react-draggable"; // The default
import { connect } from "react-redux";

class ToneButton extends React.Component {
  constructor(props) {
    super(props);
    this.selector = React.createRef();
    this.state = {
      deltaPosition: {
        x: 0,
        y: 0
      }
    };
    this.handleStop = this.handleStop.bind(this);
  }

  snapToLoop() {
    const centerPoint = window.scrollTop() + window.height() / 2;
  }

  handleStop() {
    this.rect = this.selector.current.getBoundingClientRect();
    console.log(
      "Coordinates: " + this.rect.left + "px, " + this.rect.top + "px"
    );
    console.log("Center: " + window.scrollTop() + window.height() / 2);
    this.setState({
      deltaPosition: {
        x: 0,
        y: 0
      }
    });
  }

  render() {
    return (
      <React.Fragment>
        <button
          className="hover-shadow"
          style={{
            borderRadius: "100%",
            border: "2px solid",
            borderColor: this.props.color,
            backgroundColor: "transparent",
            width: "2rem",
            height: "2rem",
            position: "absolute",
            outline: "none",
            pointerEvents: "none"
          }}
        />
        <Draggable position={this.state.deltaPosition} onStop={this.handleStop}>
          <button
            ref={this.selector}
            className="hover-shadow"
            style={{
              borderRadius: "100%",
              backgroundColor: this.props.color,
              width: "2rem",
              zIndex: 1,

              height: "2rem",
              border: "none",
              outline: "none"
            }}
          />
        </Draggable>
      </React.Fragment>
    );
  }
}

export default connect()(ToneButton);
