import React from "react";
import "../styles/index.css";

import Draggable from "react-draggable"; // The default
import { connect } from "react-redux";

function dist(x1, y1, cx, cy) {
  var a = x1 - cx;
  var b = y1 - cy;
  return Math.sqrt(a * a + b * b);
}

class ToneButton extends React.Component {
  constructor(props) {
    super(props);
    this.selector = React.createRef();
    this.state = {
      deltaPosition: {
        x: 0,
        y: 0
      }
    };
    this.handleStop = this.handleStop.bind(this);
  }

  shouldSnap(x1, y1, cx, cy) {
    //this should be a list of raddii gotten from store
    var radii = [300];
    var distToOrigin = dist(x1, y1, cx, cy);

    var index = 0;
    var curr = radii[0];
    var diff = Math.abs(distToOrigin - curr);
    for (var i = 0; i < radii.length; i++) {
      var newdiff = Math.abs(distToOrigin - radii[i]);
      if (newdiff < diff) {
        diff = newdiff;
        curr = radii[i];
        index = i;
      }
    }
  }

  handleStop() {
    this.rect = this.selector.current.getBoundingClientRect();
    const x = this.rect.left;
    const y = this.rect.top;
    const centerPoint = { x: window.innerWidth / 2, y: window.innerHeight / 2 };

    this.setState({
      deltaPosition: {
        x: 0,
        y: 0
      }
    });
  }

  render() {
    return (
      <React.Fragment>
        <button
          className="hover-shadow"
          style={{
            borderRadius: "100%",
            border: "2px solid",
            borderColor: this.props.color,
            backgroundColor: "transparent",
            width: "2rem",
            height: "2rem",
            position: "absolute",
            outline: "none",
            pointerEvents: "none"
          }}
        />
        <Draggable position={this.state.deltaPosition} onStop={this.handleStop}>
          <button
            ref={this.selector}
            className="hover-shadow"
            style={{
              borderRadius: "100%",
              backgroundColor: this.props.color,
              width: "2rem",
              zIndex: 1,

              height: "2rem",
              border: "none",
              outline: "none"
            }}
          />
        </Draggable>
      </React.Fragment>
    );
  }
}

export default connect()(ToneButton);
