import { ADD_TONE, RECEIVE_TONES, RESIZE_TONE } from "../actions/tones";

export default function tones(state = {}, action) {
  switch (action.type) {
    case ADD_TONE:
      const tone = action.tone;
      return {
        ...state,
        tones: [...state.tones, tone]
      };

    case RESIZE_TONE:
      return { state };
    case RECEIVE_TONES:
      return {
        ...state,
        ...action.tones
      };
    default:
      return state;
  }
}
