import { MAKE_INVISIBLE, CURSOR_ADD } from "../actions/cursor";

export default function shared(
  state = {
    sound: "C",
    color: "transparent",
    mode: "empty"
  },
  action
) {
  switch (action.type) {
    case CURSOR_ADD:
      return Object.assign({}, state, {
        mode: "ADD",
        sound: action.sound
      });
    case MAKE_INVISIBLE:
      return Object.assign({}, state, {
        active: false,
        color: "transparent"
      });

    default:
      return state;
  }
}
