import React from "react";
import "../styles/index.css";
import { connect } from "react-redux";
import { updateSustain } from "../action/shared";
const LibListStyle = {
  textAlign: "left",
  margin: 0,
  zIndex: 1,
  padding: 0,
  position: "relative"
};

const LibListItemStyle = {
  display: "inline-block",
  verticalAlign: "top",
  padding: ".25rem"
};

class SustainButton extends React.Component {
  handleClick() {
    this.props.dispatch(updateSustain(this.props.sustain));
  }

  render() {
    return (
      <button
        className="hover-shadow"
        style={{
          borderRadius: "100%",
          border: "2px solid",
          borderColor: "#692d54",

          backgroundColor: "transparent",
          width: this.props.size + "px",
          height: this.props.size + "px",
          position: "relative",
          marginTop: 50 - this.props.size / 2 - 30 + "px",
          outline: "none",
          pointerEvents: "none"
        }}
      >
        <div className="sustain-select">
          {" "}
          {this.props.selected ? this.props.text : null}
        </div>
      </button>
    );
  }
}
class SustainMenu extends React.Component {
  render() {
    var susToPass = ["32n", "16n", "8n", "4n", "2n", "1m", "2m"];
    var susToShow = ["1/32", "1/16", "1/8", "1/4", "1/2", "1"];
    var sizes = [20, 25, 30, 35, 40, 45, 50, 55];
    return (
      <React.Fragment>
        <h3 className="light inl-blk"> SUSTAIN</h3>
        <ul style={LibListStyle}>
          {susToShow.map((item, i) => (
            <li style={LibListItemStyle} key={item.color}>
              <SustainButton
                text={item}
                sustain={susToPass[i]}
                size={sizes[i]}
              />
            </li>
          ))}
        </ul>
      </React.Fragment>
    );
  }
}

function mapStateToProps(state) {
  return {
    selectedSustain: state.shared.selectedSustain
  };
}

export default connect(mapStateToProps)(SustainMenu);
