import React, { Component } from "react";
import { render } from "react-dom";
import { Stage, Layer, Rect, Text, Circle } from "react-konva";
import Konva from "konva";

import Portal from "./Portal";
import BottomNav from "./BottomNav";
import MountedTones from "./MountedTones";
import { connect } from "react-redux";
import reducer from "../reducers";
import middleware from "../middleware";
import { Provider } from "react-redux";
import { createStore } from "redux";
import MountedLoops from "./MountedLoops";
import Cord from "../components/Cord";
import LeftNav from "../components/LeftNav";
import { screenResize } from "../actions/shared";

const store = createStore(reducer, middleware);
window.addEventListener("resize", () => {
  store.dispatch(screenResize(window.innerWidth, window.innerHeight));
  var screenWidth = store.getState().shared.screenWidth;
  var screenHeight = store.getState().shared.screenHeight;
});

class App extends Component {
  render() {
    // Stage is a div container
    // Layer is actual canvas element (so you may have several canvases in the stage)
    // And then we have canvas shapes inside the Layer
    return (
      <React.Fragment>
        <Stage width={screenWidth} height={screenHeight}>
          <Provider store={store}>
            <MountedLoops />

            <MountedTones />
            <Cord />
          </Provider>
          <Portal>
            <Provider store={store}>
              <LeftNav />
              <BottomNav />
            </Provider>
          </Portal>
        </Stage>
      </React.Fragment>
    );
  }
}
export default App;
