import { MAKE_INVISIBLE, MAKE_VISIBLE } from "../actions/cursor";

export default function shared(
  state = {
    visible: false,
    color: "transparent"
  },
  action
) {
  switch (action.type) {
    case MAKE_VISIBLE:
      return Object.assign({}, state, {
        active: true,
        color: action.color
      });
    case MAKE_INVISIBLE:
      return Object.assign({}, state, {
        visible: false,
        color: "transparent"
      });

    default:
      return state;
  }
}
