import React from "react";
import "../styles/index.css";
import AppBar from "@material-ui/core/AppBar";
import PlayButton from "./PlayButton";
import { Line, Layer, Circle } from "react-konva";
import Toolbar from "@material-ui/core/Toolbar";
import { connect } from "react-redux";
import Konva from "konva";

class Cord extends React.Component {
  componentDidMount() {
    var period = 2000;
    var centerTension = 1;
    var amplitude = 10;
    this.anim = new Konva.Animation(frame => {
      var points = [
        this.props.center.x,
        this.props.center.y,
        this.props.center.x + 100,
        this.props.center.y - 100,
        this.props.center.x - 100,
        this.props.center.y - 200,
        this.props.center.x + 100,
        this.props.center.y - 300,
        this.props.center.x,
        this.props.center.y - 400
      ];
      this.line.x(
        amplitude * Math.sin((frame.time * 2 * Math.PI) / period) +
          centerTension
      );
    }, this.line.getLayer());

    this.anim.start();
  }
  render() {
    return (
      <Layer>
        <Circle
          x={this.props.center.x}
          y={this.props.center.y - 400}
          radius={4}
          fill="#692D55"
        />
        <Circle
          x={this.props.center.x}
          y={this.props.center.y}
          radius={15}
          fill="#692D55"
        />
        <Line
          points={[
            this.props.center.x,
            this.props.center.y,
            this.props.center.x,
            this.props.center.y - 100,
            this.props.center.x,
            this.props.center.y - 200,
            this.props.center.x,
            this.props.center.y - 300,
            this.props.center.x,
            this.props.center.y - 400
          ]}
          stroke="#692D55"
          strokeWidth={3}
          tension={1}
          ref={node => {
            this.line = node;
          }}
        />
      </Layer>
    );
  }
}

function mapStateToProps(state) {
  return {
    center: state.shared.center
  };
}

export default connect(mapStateToProps)(Cord);
